INSERT INTO `seminars` (`id`, `language_id`, `category`, `name`, `seminar_type_id`, `code_no`, `training_name`, `training_needs`, `subject`, `constant_members`, `tuition_fees`, `implementation_method`, `physician`, `language`, `description`) VALUES
(1, 3, 'Consultancy', '3S Management', 1, 'PM-2', 'You learn the basics for practicing 3S management, Which is Sweep, Sort, Set in order.', '<ul>\r\n	<li>Do　your company&rsquo;s employees understand 3S management?</li>\r\n	<li>Can your company&rsquo;s employees carry out 3S management? &nbsp;</li>\r\n	<li>Can your company&rsquo;s managers teach 3S management?</li>\r\n	<li>Can your company make 3S management habitual?</li>\r\n</ul>\r\n', 'Managers /supervisors/staff of  production  functions , others', '30 people', 'MJBC member companies (Free)     non-members (45,000 Kyat)　 　 ', 'Lecture and Exercise (participatory training)', 'You will be able to learn the basics of  the 3S management, and to carry out them.', 'Burmese (Japanese lecture and Myanmar interpreter)', '<p>1. Share the policy and effects of 3S management</p>\r\n\r\n<p>　・What is ３Ｓ?, What is 3S management?</p>\r\n\r\n<p>　・Evaluate practice level of 3S management in your company?</p>\r\n\r\n<p>　・Confirm the effects of 3S management</p>\r\n\r\n<p>　・Show the policy of 3S management, and share it</p>\r\n\r\n<p>2. Every employees carry out 3S management</p>\r\n\r\n<p>　・How to proceed with sweeping(cleaning)</p>\r\n\r\n<p>　・How to proceed with sorting(Organizing)</p>\r\n\r\n<p>　・How to proceed with setting in order(Pick up)</p>\r\n\r\n<p>3. Managers and supervisors lead by examples</p>\r\n\r\n<p>　・Top management lead by good example</p>\r\n\r\n<p>4. Recognize 3S management and make it habitual</p>\r\n\r\n<p>　・Standardization of 3S management</p>\r\n\r\n<p>　・Deployment Step of 3S management and the achievement level</p>\r\n\r\n<p>　・Recognition and habituation of 3S management</p>\r\n\r\n<p>5. Exercise of 3S management</p>\r\n\r\n<p>　・Exercise:&nbsp; Sweep</p>\r\n\r\n<p>　・Exercise: Sort</p>\r\n\r\n<p>　・Exercise: Set in order</p>\r\n\r\n<p>&nbsp;</p>\r\n'),
(2, 3, 'Consultancy', 'Monozukuri and Customer''s Viewpoint', 1, 'PM-0001', 'Learn practical method of customer’s viewpoint', '<p>・ Can your company&rsquo;s employees&nbsp; understand basics of &ldquo;Monozukuri&rdquo; management?</p>\r\n\r\n<p>・ Can your company&rsquo;s managers teach the basics of &ldquo;Monozukuri&rdquo; management ? &nbsp;<br />\r\n・ Can your company&rsquo;s employees understand to work in customer&rsquo;s viewpoint?</p>\r\n\r\n<p>・ Can your company&rsquo;s managers teach the basics of customer&rsquo;s viewpoint?</p>\r\n', 'Production department managers, and production department staff', '20 people', 'MJBC member companies (Free)     non-members (45,000 Kyat)　', '(It will be conducted by students participatory) exercises and lecture', 'I learn and practice the basics of customer''s perspective the basics of manufacturing', 'Burmese (Japanese lecture and Myanmar interpreter)', '<p>1. The Basics of &ldquo;Monozukuri&rdquo; Management</p>\r\n\r\n<p>　・Ideal Image of &ldquo;Monozukuri&rdquo; Compnay</p>\r\n\r\n<p>　・The Basics of &ldquo;Monozukuri&rdquo; Management</p>\r\n\r\n<p>　・Roles of Production Function for &ldquo;monozukuri&rdquo;</p>\r\n\r\n<p>&nbsp; ・Actions to realize the Basics of &ldquo;Monozukuri&rdquo; Management</p>\r\n\r\n<p>　・International Standards of &ldquo;Monodukuri&rdquo; Management</p>\r\n\r\n<p>　・BMP（Better Management Practice)</p>\r\n\r\n<p>　・Exercise: Practices of&nbsp; &ldquo;Monozukuri&rdquo; Management</p>\r\n\r\n<p>2. Definition and Process of &nbsp;Customer&rsquo;s Viewpoint</p>\r\n\r\n<p>　・Definition of Customer&rsquo;s Viewpoint</p>\r\n\r\n<p>　・Process of Customer&rsquo;s Viewpoint</p>\r\n\r\n<p>3. Practices of Customer&rsquo;s Viewpoint</p>\r\n\r\n<p>　・Know Your Customer</p>\r\n\r\n<p>　・Grasp what customer wants</p>\r\n\r\n<p>　・Meet Customer&rsquo;s Expectation</p>\r\n\r\n<p>　・Exercise: Self-Evaluation for Practice level of&nbsp; Customer&rsquo;s Viewpoint</p>\r\n\r\n<p>4. Exercise: Constitution Development of Customer&rsquo;s Viewpoint</p>\r\n\r\n<p>　・Exercise: Constitution Development of Customer&rsquo;s Viewpoint</p>\r\n'),
(5, 3, 'Executive Search', 'Human Resources Management', 5, 'SM-0002', 'Learn 10 basics of HRM which the executives should execute', '<p>・Do you understand the importance of human resources management?</p>\r\n\r\n<p>・Do you show the policy of human resources management?</p>\r\n\r\n<p>・Are you aware of the problem of human resource management of the company ?</p>\r\n\r\n<p>・Do you think about the loyalty and motivation of your employees?</p>\r\n', 'Managing Director, Directors, Factory Managers, General Managers, etc.', '30 people (up to 100 Persons)', 'MJBC member companies (Free)     non-members (50,000 Kyat)', 'SH_mehtod', 'You can know the essences of HRM which are essential to the development of Myanmar.', 'Burmese (Japanese lecture and Myanmar interpreter)', '<p>1. What is the HRM?</p>\r\n\r\n<p>　2. HRM is&nbsp; a success requirements of the company.</p>\r\n\r\n<p>　3. Employee Classification and Job Ranking</p>\r\n\r\n<p>　4. Key Points of Recruitment Management</p>\r\n\r\n<p>　5. Key Points of Training and Development Management</p>\r\n\r\n<p>　6. Key points of Staffing Management</p>\r\n\r\n<p>　7. Key Points of Performance Appraisal System</p>\r\n\r\n<p>　8. Key Points of Compensation Management</p>\r\n\r\n<p>　9. Key Points of Labor Management</p>\r\n\r\n<p>10. Implementation of HRM audit</p>\r\n'),
(6, 3, 'Executive Search', 'Performance Appraisal Management', 1, 'HRM-4', 'Training Name', '<p>・Can a HR &nbsp;staff of your company design and apply performance appraisal system?</p>\r\n\r\n<p>・Don&rsquo;t &nbsp;your company&rsquo;s employees have a complaint regarding performance appraisal?</p>\r\n\r\n<p>・Can a HR manager of your company carry out properly the performance appraisal?</p>\r\n\r\n<p>・Does your company have a problem related to the performance appraisal system?</p>\r\n', 'HR managers, HR Staff, Management people who have problems on performance appraisal ', '30 people', 'MJBC member companies (Free)     non-members (45,000 Kyat)　　', 'New_Method', 'You will be able to learn the basics of performance appraisal system, and to carry out them.', 'Burmese (Japanese lecture and Myanmar interpreter)', '<p>1. Roles of HR staff in charge of performance appraisal system.</p>\r\n\r\n<p>　・Human Resources Management Cycle</p>\r\n\r\n<p>　・Roles of HR staff in charge of&nbsp; performance appraisal system</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>2. Basics and practices of performance appraisal system</p>\r\n\r\n<p>　・Exercise: Questions related to performance appraisal system</p>\r\n\r\n<p>　・The objective and function of performance appraisal system</p>\r\n\r\n<p>&nbsp; ・Principle of performance appraisal system</p>\r\n\r\n<p>　・PDCA of performance appraisal system</p>\r\n\r\n<p>　・Management by objective and performance appraisal</p>\r\n\r\n<p>　・Designing performance appraisal system</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>3. Exercise: Practices of performance appraisal system</p>\r\n\r\n<p>　・Exercise: Questions and countermeasures of your company</p>\r\n\r\n<p>　・Exercise: Feedback interview of performance appraisal</p>\r\n'),
(7, 3, 'Executive Search', 'Promotion and Rewards Management', 5, 'SH0003', 'You learn about various rewards measures and promotion system necessary to hire and retain employees with talents.', '<p>・Can your company hire employees with talents, and retain them?</p>\r\n\r\n<p>・Can your company reward for the contribution of&nbsp; the employees?</p>\r\n\r\n<p>・Are your company&rsquo;s employees satisfied with the promotion system?</p>\r\n\r\n<p>・Are your company&rsquo;s employees satisfied with the reward system ?</p>\r\n', 'HR managers, HR Staff, Management people who have problems on labor management ', '30 people', 'MJBC member companies (Free)     non-members (45,000 Kyat)　　 ', 'SH_mehtod', 'You will be able to learn the basics of promotion management, and to carry out them.', 'Burmese (Japanese lecture and Myanmar interpreter)', '<p>1. Roles of HR function in charge of&nbsp; promotion/rewards management</p>\r\n\r\n<p>　・What is HRM ?/ Roles of HR function in charge of&nbsp; promotion management</p>\r\n\r\n<p>2. Practices of various rewards measures</p>\r\n\r\n<p>　・Objective of rewards management</p>\r\n\r\n<p>　・Rewards by compensation(wages and benefits)</p>\r\n\r\n<p>&nbsp;&nbsp; ・Rewards by promotion and qualification</p>\r\n\r\n<p>　・Rewards by awards</p>\r\n\r\n<p>　・Rewards by attractive job and training</p>\r\n\r\n<p>3. Basics of promotion management</p>\r\n\r\n<p>　・Basics of promotion management</p>\r\n\r\n<p>　・How to proceed with promotion management</p>\r\n\r\n<p>　・Concept of promotion criteria</p>\r\n\r\n<p>　・Changes of Grade System in Japan and the US　&nbsp;</p>\r\n\r\n<p>4. Skill based qualification system</p>\r\n\r\n<p>　・Structure of Skill based Qualification System</p>\r\n\r\n<p>5. Job based grade system</p>\r\n\r\n<p>　・Structure of Job based Grade System</p>\r\n\r\n<p>　・Job Analysis and Job Description</p>\r\n\r\n<p>6. Exercise: Practices of Labor Management in your company</p>\r\n\r\n<p>　・Exercise: Problems and Countermeasures on the Rewards</p>\r\n\r\n<p>　・Exercise: Problems and Countermeasures on the Promotion</p>\r\n'),
(8, 3, 'Seminar', 'Operation Standard training', 1, 'PM-3', 'You learn the basics of how to make an operation standard necessary to produce good products without variation.', '<ul>\r\n	<li>Does&nbsp; your company create operation standard for each process?</li>\r\n	<li>Do your company&rsquo;s employees work based on the operation standard? &nbsp;</li>\r\n	<li>Can your company&rsquo;s managers and supervisors create an operation standard and teach it?</li>\r\n	<li>Does your company revise properly the&nbsp; operation standard?</li>\r\n</ul>\r\n', 'Managers /supervisors/staff of  production  functions , others', '30 people', 'MJBC member companies (Free)     non-members (45,000 Kyat)', 'Lecture and Exercise (participatory training)', 'You will be able to learn how to make  an operation standard, and to carry out it.', 'Burmese (Japanese lecture and Myanmar interpreter)', '<p>1. Operation standard and its effects</p>\r\n\r\n<ul>\r\n	<li>　What is&nbsp; an operation standard?</li>\r\n	<li>　What are effects of&nbsp; the operation standard?</li>\r\n	<li>　Evaluate an operation standard of your company</li>\r\n</ul>\r\n\r\n<p>2. Creation of operation standard</p>\r\n\r\n<ul>\r\n	<li>　Creating point of operation standard</li>\r\n	<li>　Easy to use operation standard</li>\r\n	<li>　How to make an operation standard</li>\r\n</ul>\r\n\r\n<p>3. Teaching by an operation standard and its maintenance and its revision</p>\r\n\r\n<ul>\r\n	<li>　Teaching by an operation standard</li>\r\n	<li>　Maintenance and revision of the operation standard</li>\r\n</ul>\r\n\r\n<p>4. Recognize 3S management and make it habitual</p>\r\n\r\n<ul>\r\n	<li>&nbsp;&nbsp; Standardization of 3S management</li>\r\n	<li>　Deployment Step of 3S management and the achievement level</li>\r\n	<li>　Recognition and habituation of 3S management</li>\r\n</ul>\r\n\r\n<p>5. Exercise of making an operation standard</p>\r\n\r\n<ul>\r\n	<li>　Exercise:&nbsp; Make an operation standard for assembling&nbsp; an CD case</li>\r\n	<li>　Exercise: Make an operation standard for your subordinate&rsquo;s work</li>\r\n</ul>\r\n'),
(9, 3, 'Seminar', 'Claim Management Training', 1, 'PM-4', 'You learn the basics of how to resolve customers’ claims.', '<ul>\r\n	<li>Does&nbsp; your company set the basic rules of claim management?</li>\r\n	<li>Do your company&rsquo;s employees know the basic rules of claim management? &nbsp;</li>\r\n	<li>Can your company&rsquo;s managers and supervisors teach the claim management?</li>\r\n	<li>Are your company&rsquo;s customers satisfied with the claim management ?</li>\r\n</ul>\r\n', 'Managers /supervisors/staff of  production  functions , others', '30 people', 'MJBC member companies (Free)     non-members (45,000 Kyat)', 'Lecture and Exercise (participatory training)', 'You will be able to learn how to make  an operation standard, and to carry out it.', 'Burmese (Japanese lecture and Myanmar interpreter)', '<p>1. What is a customer satisfaction?</p>\r\n\r\n<ul>\r\n	<li>　Customer&rsquo;s viewpoint</li>\r\n	<li>　Customer satisfaction</li>\r\n	<li>　Grasp needs and wants from customers</li>\r\n	<li>　key points to increase customer satisfaction</li>\r\n</ul>\r\n\r\n<p>2. Claim and claim management</p>\r\n\r\n<ul>\r\n	<li>　What is a claim?</li>\r\n	<li>&nbsp; 5 principles of claim management&nbsp;</li>\r\n	<li>　Guidelines and initiatives of ISO 10002</li>\r\n	<li>　Evaluate the practice level of claim management&nbsp;</li>\r\n</ul>\r\n\r\n<p>3. Process of claim management</p>\r\n\r\n<ul>\r\n	<li>　Process of claim management</li>\r\n	<li>　Identify a claim</li>\r\n	<li>　make a claim report</li>\r\n	<li>　Problem-solving to prevent recurrence</li>\r\n</ul>\r\n\r\n<p>4. Communication for claim management</p>\r\n\r\n<ul>\r\n	<li>　Communication at the time of claim occurrence</li>\r\n	<li>　Techniques for listening to claim</li>\r\n	<li>　Exercise: Claim management for answering by telephone</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n'),
(10, 3, 'Seminar', 'Visual Management Training', 1, 'PM-5', 'You learn the basics of how to proceed with visual management for ensuring that normality or trouble can be confirmed at a glance.', '<ul>\r\n	<li>Do&nbsp; your company &lsquo;s employees understand a visual management ?</li>\r\n	<li>Do your company&rsquo;s managers and supervisors teach a visual management? &nbsp;</li>\r\n	<li>Has your company carried out a visual management?</li>\r\n	<li>Is your company&rsquo;s visual management&nbsp; working?</li>\r\n</ul>\r\n', 'Managers /supervisors/staff of  production  functions , others', '30 people', 'MJBC member companies (Free)     non-members (45,000 Kyat)', 'Lecture and Exercise (participatory training)', 'You will be able to learn how to proceed with visual management, and to carry out it.', 'Burmese (Japanese lecture and Myanmar interpreter)', '<p>1. Visual management</p>\r\n\r\n<ul>\r\n	<li>What is a visual management?</li>\r\n	<li>Aim of&nbsp; visual management</li>\r\n	<li>Evaluate a practice level of visual management</li>\r\n</ul>\r\n\r\n<p>2. How to proceed with visual management</p>\r\n\r\n<ul>\r\n	<li>　How to proceed with&nbsp; visual management</li>\r\n	<li>　Subject of visual management</li>\r\n	<li>　Practice points of visual management</li>\r\n</ul>\r\n\r\n<p>3. Concrete development of visual management</p>\r\n\r\n<ul>\r\n	<li>　Visualization of safety and health management</li>\r\n	<li>　Visualization of quality management</li>\r\n	<li>　Visualization of cost management</li>\r\n	<li>　Visualization of delivery management</li>\r\n	<li>　Visualization of production management</li>\r\n	<li>　Visualization of actual product management</li>\r\n	<li>　Visualization of work management</li>\r\n	<li>　Visualization of equipment management</li>\r\n	<li>　Visualization of information management</li>\r\n	<li>　Visualization of management by policy</li>\r\n</ul>\r\n\r\n<p>4. Exercise: Introduction of visual management</p>\r\n\r\n<ul>\r\n	<li>Exercise: Item of visual management in your company</li>\r\n	<li>Exercise:&nbsp; Practice points to introduce a visual management</li>\r\n</ul>\r\n'),
